package com.example.rpc.loginService;import org.springframework.stereotype.Service;import org.springframework.util.CollectionUtils;import java.util.HashMap;import java.util.Map;//@Servicepublic class LoginServiceImpl implements LoginService {    @Override    public String login(Map<String, String> map) {        Map<String, String> response = new HashMap<>();        if (CollectionUtils.isEmpty(map)) {            response.put("code", "0");            response.put("msg", "登录失败");        }        return null;    }}