package com.example.rpc.util;import com.example.rpc.config.RPCConfig;import com.example.rpc.service.StudentService;//import com.example.rpc.service.StudentService1;import org.springframework.context.ApplicationContext;import org.springframework.context.annotation.AnnotationConfigApplicationContext;import java.util.HashMap;import java.util.Map;public class AppMain {    public static void main(String[] args) throws Exception {        Map<String, String> params = new HashMap<>();        params.put("1", "v1");        ApplicationContext context = new AnnotationConfigApplicationContext(RPCConfig.class);        Object studentService = context.getBean("StudentService");        StudentService s = (StudentService) studentService;        String s1 = s.sayHello(params);        System.out.println("s1 = " +s1);//        StudentService1 studentService1 = (StudentService1) context.getBean("StudentService1");//        System.out.println("s2=" + studentService1.sayHello(params));//    }}