package com.example.rpc.util.annotation;import com.example.rpc.util.annotationImpl.EnableRPCImpl;import org.springframework.context.annotation.Import;import java.lang.annotation.*;/** * 开请自定义RPC注解，注测代理组件以及Reference组件 */@Target({ElementType.TYPE, ElementType.FIELD})@Retention(RetentionPolicy.RUNTIME)@Inherited@Import(EnableRPCImpl.class)public @interface EnableRPC {    String[] basePackage() default {};}